mapper.select:
(select * from tbl_board where b_category = '공지' order by id desc limit 3) union all (select * from tbl_board where b_category != '공지' and (title like concat('%', #{keyword}, '%') or b_content like concat('%', #{keyword}, '%') or b_writer like concat('%', #{keyword}, '%'))
and b_category = #{searchType}
order by id desc limit #{pageStart}, #{perPageNum})
